var app = new Vue({
    el: '#app',
    data: {
        data: {}
    }
});
$(document).ready(function () {
    /* handle tables */
    var table = $('#bank-table').DataTable({
        responsive: true,
        "scrollX": true,
        processing: true,
        serverSide: true,
        ajax: APP_URL + '/bank/datatables',
        columns: [

            {
                data: "id",

                render: function (data, type, row, meta) {
                    return meta.row + meta.settings._iDisplayStart + 1;
                },
            },
            {
                data: 'bname',
                name: 'bname',
                targets: 2,
                orderable: false,
                render: function (data, type, row) {
                    var picture = "<img src='/storage/" + row['pic'] +"'width='30' class='img-thumbnail'> "+data+"";
                  
                   
                    return picture;
                },
            },
            {
                data: 'number',
                name: 'number',

            },
            {
                data: 'name',
                name: 'name',

            },
            {
                data: 'id',
                name: 'id'
            }
        ],
 
        columnDefs: [{
            targets: 4,
            orderable: false,
            render: function (data, type, row) {
                var dataName = row['bname'];
                var btnEdit = '<a href="#" data-href="' + APP_URL + '/bank/form/' + data + '" data-modal-name="ajaxModal" role="button" data-id="' + data + '" class="btn btn-outline-dark btn-sm btn-edit" title="แก้ไข"><i class="fa fa-edit"></i></a> ';
                var btnDelete = '<a href="#" data-href="' + APP_URL + '/bank/' + data + '" data-id="' + data + '" data-name="' + dataName + '" role="button" class="btn btn-outline-danger btn-sm btn-delete" title="ลบ"><i class="fa fa-trash"></i></a>';
                // var btnView = '<a href="#" data-href="' + APP_URL + '/user/view/' + row.id + '" data-modal-name="ajaxModal" data-id="' + row.id + '" class="btn-edit">' + data + '</a> ';
                btnDelete = APP_USERID != data ? btnDelete : '';
                return btnEdit + btnDelete;
            },
        }]
        
    });
    
    /* handle validate */
    $('#ajaxModal').on('shown.bs.modal', function (e) {
        $('#saveForm').validate({
            submitHandler: function (form) {
                $.ajaxSetup({
                    headers: {
                        'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                    }
                });
                var id = $('input[name=id]').val();
                var url = APP_URL + '/bank';
                saveForm(id, url, table);
            },
            
            rules: {
                bname: {
                    required: true
                },
                number: {
                    required: true
                },
                name: {
                    required: true
                }
            },
            messages: {},
            errorElement: 'span',
            errorPlacement: function (error, element) {
                error.addClass("error-block");
                error.addClass("invalid-feedback");
                if (element.prop("type") === "checkbox") {
                    error.insertAfter(element.parent("label"));
                } else if (element.parent('.input-group').length) {
                    error.insertAfter(element.parent()); /* radio checkbox? */
                } else if (element.hasClass('select2')) {
                    error.insertAfter(element.next('span')); /* select2 */
                } else {
                    error.insertAfter(element);
                }
            },
            highlight: function (element, errorClass, validClass) {
                $(element).parents('.form-group').addClass('has-error').removeClass('has-success');
                $(element).addClass('is-invalid').removeClass('is-valid');
            },
            unhighlight: function (element, errorClass, validClass) {
                $(element).parents('.form-group').addClass('has-success').removeClass('has-error');
                $(element).addClass('is-valid').removeClass('is-invalid');
            }
        });
    });

    /* handle delete */
    $('body').on('click', '.btn-delete', function (e) {
        e.preventDefault();
        var url = $(this).data('href');
        var name = $(this).data('name');
        var callback = function () {
            deleteForm(url, table);
        }

        confirmBox('ลบข้อมูล ' + name, callback);
    });

    /* handle upload file */
    var openModal = false;
    $('#ajaxModal').on('shown.bs.modal', function (e) {
        $('body').on('change', '#file', function (e) {
            if (openModal) return false;
            openModal = true;
            e.preventDefault();
            loadingCustom();
            $.ajaxSetup({
                headers: {
                    'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
                }
            });

            var form_data = new FormData();
            form_data.append('file', this.files[0]);
            $.ajax({
                url: APP_URL + '/bank/uploadfile',
                data: form_data,
                type: 'POST',
                contentType: false,
                processData: false,
                success: function (resp) {
                    $.LoadingOverlay('hide');
                    if (resp.status === 'success') {
                        imgsrc = 'storage/' + resp.data;
                        $('#file_name').val(resp.data);
                        $('#filecancle').val(resp.data);
                        $('.img-thumbnail').attr('src',imgsrc);
                        $('.img-thumbnail').removeClass('invisible').addClass('visible');
                        //$('.btn-view-file').attr('href', APP_LINK + resp.data);
                        openModal = false;
                    } else {
                        showBox(resp.message.file[0], 'error');
                        //$('#saveForm')[0].reset();
                        $('#file').val('');
                        openModal = false;
                        $('.img-thumbnail').removeClass('visible').addClass('invisible');

                        // $('#ajaxModal').modal('hide');


                    }
                },
                error: function (xhr, textStatus, errorThrown) {
                    $.LoadingOverlay('hide');
                    showBox(textStatus, 'error', errorThrown);
                }
            });
        });
    });


   

});
