            var api = this.api();
            var rows = api.rows({ page: 'current' }).nodes();
            var last = null;

            api.column(0, { page: 'current' }).data().each(function (group, i) {
                if (last !== group) {

                    $(rows).eq(i).before(
                        '<tr class="group table-secondary text-left "><td colspan="13">' + group + '</td></tr>'
                    );

                    last = group;
                }
            });
