var app = new Vue({
    el: '#app',
    data: {
        data: {}
    }
});
$(document).ready(function () {
    /* handle tables */
    // var idwork = $('#work').val();
    // alert(idwork);
    var table = $('#options-table').DataTable({

        responsive: true,
        scrollY: "true",
        scrollX: "true",
        processing: true,
        serverSide: true,
        ajax: APP_URL + '/service/datatableswaitprice/',

        columns: [

            {
                data: "service_code",

                render: function (data, type, row, meta) {
                    var i;
                    var number = '';
                    for (i = 3; i > data.length; i--) {
                        number = number + '0';
                    }
                    var code = number + data;

                    var btnView = '<a href="/service/waitpriceD/' + row['service_id'] + '"  title="รายละเอียด">' + code + '</a> ';
                    return btnView;
                },
            },
            {
                data: 'workname',
                name: 'workname',
                className: "text-left",
                render: function (data, type, row) {

                    var btnView = '<a href="/service/waitpriceD/' + row['service_id'] + '"  title="รายละเอียด">' + data + '</a> ';
                    return btnView;
                },
            },
            {
                data: 'namec',
                name: 'namec',
                render: function (data, type, row) {

                    var btnView = '<a href="/service/waitpriceD/' + row['service_id'] + '"  title="รายละเอียด">' + data + '</a> ';
                    return btnView;
                },
            },
            {
                data: 'service_status',
                name: 'service_status',
                render: function (data, type, row) {

                    var btnView = '<a href="/service/waitpriceD/' + row['service_id'] + '"  title="รายละเอียด">' + row['service_status'] + '</a> ';
                    return btnView;
                },
            },
            {
                data: 'name',
                name: 'name',
                render: function (data, type, row) {

                    var btnView = '<a href="/service/waitpriceD/' + row['service_id'] + '"  title="รายละเอียด">' + data + '</a> ';
                    return btnView;
                },
            },

            {
                data: 'service_id',
                name: 'service_id'
            }, {

                data: 'service_id',
                orderable: false,
                render: function (data, type, row) {
                    textdata = '';
                    return textdata;
                },
            }
        ],

        columnDefs: [{
            targets: 5,
            orderable: false,
            render: function (data, type, row) {
                var dataName = row['namec'];
                if (row['locationc']) {
                    var btnLocation = '<a href="' + row['locationc'] + '" target="_blank"  class="btn btn-outline-dark btn-sm" title="สถานที่"><i class="fas fa-map-marker-alt"></i></a> ';
                } else { var btnLocation = ''; }
                if (row['template_id']) { var btnGear = '<a "#" data-href="waitpricestatus/' + data + '"  data-modal-name="ajaxModal"  class="btn btn-outline-dark btn-sm btn-create" role="button" title="ตั้งค่า"><i class="fas fa-cog"></i></a> ';} else {
                    var btnGear = '';
                }
                var btnEdit = '<a href="/service/updatesurveyf/' + data + '"  class="btn btn-outline-dark btn-sm" title="แก้ไข"><i class="fa fa-edit"></i></a> '; 
                // var btnDelete = '<a href="#" data-href="' + APP_URL + '/service/pledge/' + data + '" data-id="' + data + '" data-name="' + dataName + '" role="button" class="btn btn-outline-danger btn-sm btn-delete" title="ลบ"><i class="fa fa-trash"></i></a>';
                // var btnView = '<a href="#" data-href="' + APP_URL + '/user/view/' + row.id + '" data-modal-name="ajaxModal" data-id="' + row.id + '" class="btn-edit">' + data + '</a> ';
                // btnDelete = APP_USERID != data ? btnDelete : '';
                return btnLocation + btnGear + btnEdit;
            },
        }]
    });

    /* handle tables */

    /* handle validate */
    $('#saveForm').on('shown.bs.modal', function (e) {
        $('#saveForm').validate({
            submitHandler: function (form) {
                var id = $('input[name=id]').val();
                var url = APP_URL + '/pledgestatusu';
                saveForm(id, url, table);
            },
            rules: {
                pledge_status: {
                    required: true
                }
            },

            errorElement: 'span',
            errorPlacement: function (error, element) {
                error.addClass("error-block");
                error.addClass("invalid-feedback");
                if (element.prop("type") === "checkbox") {
                    error.insertAfter(element.parent("label"));
                } else if (element.parent('.input-group').length) {
                    error.insertAfter(element.parent()); /* radio checkbox? */
                } else if (element.hasClass('select2')) {
                    error.insertAfter(element.next('span')); /* select2 */
                } else {
                    error.insertAfter(element);
                }
            },
            highlight: function (element, errorClass, validClass) {
                $(element).parents('.form-group').addClass('has-error').removeClass('has-success');
                $(element).addClass('is-invalid').removeClass('is-valid');
            },
            unhighlight: function (element, errorClass, validClass) {
                $(element).parents('.form-group').addClass('has-success').removeClass('has-error');
                $(element).addClass('is-valid').removeClass('is-invalid');
            }
        });
    });



    //สร้างปุ่มใน Datatable
    /* handle delete */
    $('body').on('click', '.btn-delete', function (e) {
        e.preventDefault();
        var url = $(this).data('href');
        var name = $(this).data('name');
        var callback = function () {
            deleteForm(url, table);
        }

        confirmBox('ลบข้อมูล ' + name, callback);
    });
});
